bled /><input type='hidden' name='q' value='$q' /><input type='hidden' name='o' value='$ordby' /><input type='hidden' name='p' value='1' /></form></div>";$previous = $pagenum-1;echo " <div class='searchbar'><form name='formpageprevious' id='formpageprevious' method='post' action='home.php'><input type='submit' name='previous' value='<' $firstbuttonenabled /><input type='hidden' name='q' value='$q' /><input type='hidden' name='o' value='$ordby' /><input type='hidden' name='p' value='$previous' /></form></div>";//This does the same as above, only checking if we are on the last page, and then generating the Next and Last linksif ($pagenum == $last) {	$lastbuttonenabled = "disabled='disabled'";}else{	$lastbuttonenabled = "";}$next = $pagenum+1;echo " <div class='searchbar'><form name='formpagenext' id='formpagenext' method='post' action='home.php'><input type='submit' name='next' value='>' $lastbuttonenabled /><input type='hidden' name='q' value='$q' /><input type='hidden' name='o' value='$ordby' /><input type='hidden' name='p' value='$next' /></form></div>";echo " <div class='searchbar'><form name='formpagelast' id='formpagelast' method='post' action='home.php'><input type='submit' name='last' value='>>' $lastbuttonenabled /><input type='hidden' name='q' value='$q' /><input type='hidden' name='o' value='$ordby' /><input type='hidden' name='p' value='$last' /></form></div>";echo "<div class='searchbar'>Page $pagenum of $last &nbsp;</div>";		echo "<div class='searchbar'>| $num_rows $results	</div></div>	</div></body></html>"; }?>